import React, {useState} from 'react'
import styled from 'styled-components'
import MenuIcon from '@material-ui/icons/Menu'
import CloseIcon from '@material-ui/icons/Close';
import {selectCars} from '../features/car/carSlice'
import {useSelector} from 'react-redux'
const Header = () => {

    const [burgerStatus, setBurgerStatus] = useState(false);
    const cars = useSelector(selectCars);
    const [navbar, setNavBar] = useState(false);


    const changeBackground = () => {
        if(window.scrollY >= 60) {
            setNavBar(true)
        } else  {
            setNavBar(false)
        }
    }

    window.addEventListener('scroll', changeBackground)
  return (
    <Container className={navbar ? 'Container:active' : 'Container'}>
        <a>
            <img src="/images/logo.svg" />
        </a>
        <Menu>
            {cars && cars.map((car, index) => (
            <a key={index} href="#">{car}</a>
            ))}
        </Menu>

        <RightMenu>
            <a href="#"> Shop</a>
            <a href="#">Tesla Account</a>
            <CustomMenu onClick={() => setBurgerStatus(true)} />
        </RightMenu>
        <BurgerNav show={burgerStatus}>
            <CloseWrapper>
            <CustomClose onClick={() => setBurgerStatus(false)}/>
            </CloseWrapper>
            {cars && cars.map((car, index) => (
            <li key={index}><a hred="#">{car}</a></li>
            ))}
            <li><a hred="#">Exisiting Inventory</a></li>
            <li><a hred="#">Used Inventory</a></li>
            <li><a hred="#">Trade-in</a></li>
            <li><a hred="#">Cybertruck</a></li>
            <li><a hred="#">Roadaster</a></li>
            <li><a hred="#">FAQs</a></li>
            <li><a hred="#">About us</a></li>

        </BurgerNav>

    </Container>
  )
}

export default Header

const Container = styled.div`
min-height: 60px;
position: fixed;
display: flex;
align-items: center;
justify-content: space-between;
padding: 0 20px;
top: 0;
left: 0;
right: 0;
z-index: 1;

Container:active {
    background: white;
}
`

const Menu = styled.div`
display: flex;
align-items: center;
justify-content: center;
flex: 1;

a {
    font-weight: 600;
    text-transform: uppercase;
    padding: 0 10px;
    flex-wrap: nowrap;
}

@media(max-width: 768px) {
    display:none;
}
`

const RightMenu = styled.div`
display: flex;
align-items: center;
a {
    font-weight: 600;
    text-transform: uppercase;
    margin-right: 10px;
}
`

const CustomMenu = styled(MenuIcon)`
cursor: pointer;
`

const BurgerNav = styled.div`
    position: fixed;
    top: 0px;
    bottom: 0px;;
    right: 0px;
    background: white;
    width: 300px;
    z-index: 100px;
    list-style: none;
    padding: 20px;
    display: flex;
    flex-direction: column;
    text-align: end;
    transform: ${props => props.show ? 'translateX(0%)' : 'translateX(100%)'};
    transition: transform 0.2s;
    li {
        padding: 15px 0;
        border-bottom: 1px solid rgba(0,0,0, .2);
    }

    a {
        font-weight: 600;
    }

`

const CustomClose = styled(CloseIcon)
`
cursor: pointer;
`

const CloseWrapper = styled.div`

`
